typedef struct cell_s {
	struct cell_s *_u;
	struct cell_s *_d;
	struct cell_s *_l;
	struct cell_s *_r;
	struct cell_s *_p;

	int _x;
	int _y;

	int _id;
	int _path;
} cell_t;

void gencell(cell_t *cell) {
	int corresp[16] = {
		2,
		2,
		2,

		0x0F,
		0x0F,
		0x0F,
		0x03,
		0x0C,

		0x09,
		0x0A,
		0x06,
		0x05,

		0x0D,
		0x0B,
		0x0E,
		0x07
	};

	if(cell->_id) return;
	cell->_id = corresp[rand() & 0x0F];
}

uint32_t genpix(float red, float green, float blue) {
	uint32_t pix = 0;

	if(red > 1) red = 1;
	if(red < 0) red = 0;
	if(green > 1) green = 1;
	if(green < 0) green = 0;
	if(blue > 1) blue = 1;
	if(blue < 0) blue = 0;

	pix = red * 255;
	pix <<= 8;
	pix |= (int) (green * 255);
	pix <<= 8;
	pix |= (int) (blue * 255);

	return pix | 0xFF000000;
}

OSPimage *texture(void *context, char *path) {
	static OSPimage *ret = 0;
	return context && path ? ret = OSPImgLoad(context, path, 0) : ret;
}

void drawcell(OSPwindow *wnd, OSPimage *img, cell_t *cell,
				int curx, int cury) {
	int x = cell->_x;
	int y = cell->_y;
	int lx = -(SIZE >> 1);
	int ly = -(SIZE >> 1);
	int ex;
	int ey;

	int selected = 0;

	if(x == curx && y == cury) {
		selected = 1;
	}

	x *= SIZE;
	y *= SIZE;

	ex = x + SIZE;
	ey = y + SIZE;

	for(y = ey - SIZE; y < ey; y++) {
		for(x = ex - SIZE; x < ex; x++) {
			float grad = 1. - sqrt((lx * lx + ly * ly) / 10000.);

			img->_data[y][x] = genpix(0, grad / 3., grad / 2.);

			lx++;
		}

		lx = -(SIZE >> 1);
		ly++;
	}

	x = ex - SIZE;
	y = ey - SIZE;

	switch(cell->_id) {
	case 1:
	case 2:
		OSPBlit(texture(0, 0), img, 0, SIZE * cell->_id, x, y, SIZE, SIZE);
		break;
	default:
		ey = 3;
		for(ex = 0x01; ex & 0x0F; ex <<= 1) {
			if(cell->_id & ex) OSPBlit(texture(0, 0), img, 0, SIZE * ey, x, y, SIZE, SIZE);
			ey++;
		}
	}

	if(selected) {
		OSPBlit(texture(0, 0), img, 0, 0, x, y, SIZE, SIZE);
	}

	OSPBlit(img, wnd, x, y, x, y, SIZE, SIZE);
}

int check(cell_t *cell, cell_t **shortest) {
	int dir;
	int ret = 0;

	for(dir = 0x08; dir; dir >>= 1) {
		int opdir;
		cell_t *celldir;

		switch(dir) {
		case 0x01:
			opdir = 0x02;
			celldir = cell->_u;
			break;

		case 0x02:
			opdir = 0x01;
			celldir = cell->_d;
			break;

		case 0x04:
			opdir = 0x08;
			celldir = cell->_l;
			break;

		default:
			opdir = 0x04;
			celldir = cell->_r;
		}

		if(celldir) {
			switch(celldir->_id) {
			case 2:
				if((cell->_id == 1) || (cell->_id & dir)) {
					celldir->_path = cell->_path + 1;

					if(!shortest[0] || !shortest[0]->_path ||
						(shortest[0]->_path > celldir->_path)) {

						shortest[0] = celldir;
					}

					return 1;
				}
				break;

			default:
				if(((cell->_id == 1) || (cell->_id & dir)) &&
					((celldir->_path > cell->_path + 1) || !celldir->_path) &&
					(celldir->_id & opdir)) {

					celldir->_path = cell->_path + 1;
					ret |= check(celldir, shortest);
				}

			case 1:;
			}
		}
	}

	if(!ret) cell->_path = 0;
	return ret;
}

void trace(cell_t *root) {
	int dir;

	if(root->_id == 1) return;

	for(dir = 0x08; dir; dir >>= 1) {
		int opdir;
		cell_t *celldir;

		switch(dir) {
		case 0x01:
			opdir = 0x02;
			celldir = root->_u;
			break;

		case 0x02:
			opdir = 0x01;
			celldir = root->_d;
			break;

		case 0x04:
			opdir = 0x08;
			celldir = root->_l;
			break;

		default:
			opdir = 0x04;
			celldir = root->_r;
		}

		if(celldir) {
			if(((root->_id & dir) || (root->_id == 2)) &&
				((celldir->_id & opdir) || (celldir->_id == 1)) &&
				(celldir->_path == (root->_path - 1))) {

				celldir->_p = root;
				trace(celldir);
				break;
			}
		}
	}
}

cell_t *resolve(cell_t cell[WIDTH][HEIGHT]) {
	// Resolve last
	int x;
	int y;

	cell_t *result[2] = {0};

	for(y = 0; y < HEIGHT; y++) {
		for(x = 0; x < WIDTH; x++) {
			if(cell[x][y]._id == 1) {
				result[0] = &cell[x][y];
				result[0]->_path = 1;

				if(check(result[0], &result[1])) {
					trace(result[1]);

					for(y = 0; y < HEIGHT; y++) {
						for(x = 0; x < WIDTH; x++) {
							cell[x][y]._path = 0;
						}
					}

					return result[0];
				}
				else {
					cell[x][y]._path = 0;
				}
			}
		}
	}

	return 0;
}

int main(int argc, char *argv[]) {
	OSPwindow *wnd;
	OSPimage *img;
	OSPobj *trig;
	cell_t cell[WIDTH][HEIGHT] = {{{0}}};
	char texpath[50] = "demo/banana_crush_saga/banana_default_texture.tga";

	int x;
	int y;
	int cx = 0;
	int cy = 0;
	int run = 1;
	int tmo = 16;
	int fall = 1;
	cell_t *start;

	enum {
		MENU,
		NETWORK,
		CHOOSE,
		RUN,
		FALL
	} state = CHOOSE;

	OSPrint(2, 0);
	srand(time(0));

	if(argc > 1) strcpy(texpath, argv[1]);

	/* Create objects */
	wnd = OSPWnd(0, "Banana crush saga!!!", 0, 0, WIDTH * SIZE, HEIGHT * SIZE, 0x00000000);

	if(!wnd) {
		OSPFre(0);
		return 0;
	}

	img = OSPImg(wnd, WIDTH * SIZE, HEIGHT * SIZE);

	if(!img) {
		OSPFre(0);
		return 0;
	}

	if(!texture(wnd, texpath)) {
		printf("Unable to load %s : %s\n", texpath, strerror(errno));
		OSPFre(0);
		return 0;
	}

	cell[rand() % WIDTH][rand() % HEIGHT]._id = 1;

	/* Filling array */
	for(y = 0; y < HEIGHT; y++) {
		for(x = 0; x < WIDTH; x++) {
			int ix;
			int iy;
			gencell(&cell[x][y]);

			if(x) cell[x][y]._l = &cell[x - 1][y];
			if(x < (WIDTH - 1)) cell[x][y]._r = &cell[x + 1][y];
			if(y) cell[x][y]._u = &cell[x][y - 1];
			if(y < (HEIGHT - 1)) cell[x][y]._d = &cell[x][y + 1];

			cell[x][y]._x = x;
			cell[x][y]._y = y;

			drawcell(wnd, img, &cell[x][y], cx, cy);
		}
	}

#ifdef OSP_XDBE_SUPPORT
	OSPRun(&wnd->_obj, OSPWND_SWAP);
#endif

	if(start = resolve(cell)) {
		tmo = 10;
		state = RUN;
	}

	/* Main loop */
	while(run) {
		int timestat = tmo;
		trig = OSPWte(&timestat);

		if(timestat) { /* Window cancelled */
			run = 0;
		}
		else{
			uint16_t btn;

			switch(state) {
			case CHOOSE:
				OSPRun(&wnd->_obj, OSPWND_GETBTN, &btn);

				if((btn & 0x0101) == 0x0001) {
					int lcx = cx;
					int lcy = cy;
					cx = wnd->_msx / SIZE;
					cy = wnd->_msy / SIZE;

					if(((lcx == cx) && ((lcy == cy + 1) || (lcy == cy - 1))) ||
						((lcy == cy) && ((lcx == cx + 1) || (lcx == cx - 1)))) {
						int lid = cell[lcx][lcy]._id;
						cell[lcx][lcy]._id = cell[cx][cy]._id;
						cell[cx][cy]._id = lid;

						if(start = resolve(cell)) {
							tmo = 300;
							state = RUN;
						}
						else {
							cell[cx][cy]._id = cell[lcx][lcy]._id;
							cell[lcx][lcy]._id = lid;
						}
					}

					drawcell(wnd, img, &cell[lcx][lcy], cx, cy);

					if(state == RUN) {
						drawcell(wnd, img, &cell[cx][cy], -1, -1);
						cx = -1;
						cy = -1;
					}
					else {
						drawcell(wnd, img, &cell[cx][cy], cx, cy);
					}

#ifdef OSP_XDBE_SUPPORT
					OSPRun(&wnd->_obj, OSPWND_SWAP);
#endif
				}
				if((btn & 0x0404) == 0x0404) {
					cx = wnd->_msx / SIZE;
					cy = wnd->_msy / SIZE;
					cell[cx][cy]._id = 0;
					cx = -1;
					cy = -1;

					fall = 1;
					state = FALL;
				}
				break;

			case RUN:
				if(start) {
					cell_t *last = start;
					start = start->_p;
					last->_p = 0;

					if(start) {
						last->_id = 0;
						start->_id = 1;
						drawcell(wnd, img, last, cx, cy);
						drawcell(wnd, img, start, cx, cy);

#ifdef OSP_XDBE_SUPPORT
						OSPRun(&wnd->_obj, OSPWND_SWAP);
#endif
					}
				}
				else {
					fall = 1;
					state = FALL;
				}
				break;

			case FALL:
				if(fall) {
					fall = 0;

					for(y = HEIGHT - 1; y >= 0; y--) {
						for(x = 0; x < WIDTH; x++) {
							if(!cell[x][y]._id) {
								if(y) {
									cell[x][y]._id = cell[x][y - 1]._id;
									cell[x][y - 1]._id = 0;
								}
								else gencell(&cell[x][y]);
								drawcell(wnd, img, &cell[x][y], -1, -1);
								fall = 1;
							}
						}
					}

#ifdef OSP_XDBE_SUPPORT
					OSPRun(&wnd->_obj, OSPWND_SWAP);
#endif
				}
				else {
					if(start = resolve(cell)) {
						state = RUN;
					}
					else {
						cx = 0;
						cy = 0;
						tmo = 16;
						state = CHOOSE;
					}
				}
				break;
			}
		}
	}

	OSPFre(0);
	return 0;
}

